import React, { useState } from "react";
import { Link } from "react-router-dom";
import DiaryDate from "./DiaryDate";
import DateButton from "./DateButton";

const DrHeader = () => {

    const [curDate, setCurDate] = useState(new Date());
    const headText = `${curDate.getFullYear()}.${curDate.getMonth() + 1}`;

    const nextMonth = () => {
        setCurDate(
            new Date(curDate.getFullYear(), curDate.getMonth() + 1, curDate.getDate())
        );
    }

    const lastMonth = () => {
        setCurDate(
            new Date(curDate.getFullYear(), curDate.getMonth() - 1, curDate.getDate())
        );
    }

    return (
        <div className="DrHeader">
            <div className="DH_left">
                <DiaryDate headText={headText} 
                    leftChild={<DateButton text={"<"} onClick={() => lastMonth()}/>}
                    rightChild={<DateButton text={">"} onClick={() => nextMonth()}/>}
                />
            </div>
            <div className="DH_rigth">
                <div className="info_box">
                    <Link to={"/new"}><i className="fa-solid fa-plus add_btn"></i></Link>
                </div>
                <div className="info_box">
                    <i className="fa-solid fa-user user_btn"></i>
                </div>
            </div>
        </div>
    );

}

export default DrHeader;
