import React from "react";
import { useNavigate } from "react-router-dom";

const DiaryItem = ({id,date,content,emotion,likeday}) => {

    const strDate = new Date(parseInt(date)).toLocaleDateString();
    const navigate = useNavigate();

    const goDetail = () => {
        navigate(`/diary/${id}`);
    };

    const goEdit = () => {
        navigate(`/Edit/${id}`);
    };

    return (
        <div className="DiaryItem">
            <div className={["emotion_img_wrapper", `emotion_img_wrapper_${emotion}`].join(" ")} onClick={goDetail}>
                <img src={process.env.PUBLIC_URL + `assets/emotion/emotion${emotion}.png`} />
            </div>
            <div className="info_wrapper" onClick={goDetail}>
                <div className="diary_date">{strDate}</div>
                <div className="diary_content">{content.slice(0, 25)}</div>
            </div>
            <div className="btn_wrapper">
                {likeday === 0 ? <i class="fa-thin fa-heart"></i> : <i class="fa-solid fa-heart"></i> }
                {/* {likeday === 0 ? <img src={process.env.PUBLIC_URL + 'assets/img/like.png'}/> : <img src={process.env.PUBLIC_URL + 'assets/img/delike.png'}/>} */}
            </div>
        </div>
    );

}

export default DiaryItem;
