import { useReducer, useState } from "react";

import TopLine from "../components/TopLine";
import DlHeader from "../components/DlHeader";
import DlSection from "../components/DlSection ";
import DrHeader from "../components/DrHeader";
import DiaryEditor from "./DiaryEditor";
import DiaryList from "./DiaryList";
import { useNavigate } from "react-router-dom";

const Section = ({pageView}) => {

    const [curDate, setCurDate] = useState(new Date());
    const headText = `${curDate.getFullYear()}.${curDate.getMonth() + 1}`;

    const nextMonth = () => {
        setCurDate(
            new Date(curDate.getFullYear(), curDate.getMonth() + 1, curDate.getDate())
        );
    }

    const lastMonth = () => {
        setCurDate(
            new Date(curDate.getFullYear(), curDate.getMonth() - 1, curDate.getDate())
        );
    }

    return (
        <div className="Section">
            <TopLine />
            <div className="dMain">
                <div className="dM-l">
                    <DlHeader text={"JinHyeong's Daily Life"}/>
                    <DlSection />
                </div>
                <div className="dM-r">
                    <DrHeader pageView={pageView} toDate={curDate} nextMonth={nextMonth} lastMonth={lastMonth}/>
                    <div className="DrSection">
                        { pageView === "list" ? <DiaryList /> : <DiaryEditor pageView={pageView}/> }
                    </div>
                </div>
            </div>
        </div>
    );

}

export default Section;
