import { useState } from "react";

import TopLine from "../components/TopLine";
import DlHeader from "../components/DlHeader";
import DlSection from "../components/DlSection ";
import DrHeader from "../components/DrHeader";
import DrSection from "../components/DrSection";
import DateButton from "../components/DateButton";

const Section = () => {

    const [curDate, setCurDate] = useState(new Date());
    const headText = `${curDate.getFullYear()}.${curDate.getMonth() + 1}`;


    const nextMonth = () => {
        setCurDate(
            new Date(curDate.getFullYear(), curDate.getMonth() + 1, curDate.getDate())
        );
    }

    const lastMonth = () => {
        setCurDate(
            new Date(curDate.getFullYear(), curDate.getMonth() - 1, curDate.getDate())
        );
    }

    return (
        <div className="Section">
            <TopLine />
            <div className="dMain">
                <div className="dM-l">
                    <DlHeader headText={headText} 
                        leftChild={<DateButton text={"<"} onClick={() => lastMonth()}/>}
                        rightChild={<DateButton text={">"} onClick={() => nextMonth()}/>}
                    />
                    <DlSection />
                </div>
                <div className="dM-r">
                    <DrHeader />
                    <DrSection />
                </div>
            </div>
        </div>
    );

}

export default Section;
